package maths;

public class MathsMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=10;
		int y=20;
		int a=-1;
		double b=4.67;
		
		System.out.println("Biggest number among x and y: "+Math.max(x,y));
		System.out.println("Smallest number among x and y: "+Math.min(x,y));
		System.out.println("Absolute number(Opposite to sign) of a: "+Math.abs(a));
		System.out.println("Square root of x: "+Math.sqrt(x));
		System.out.println("Rounded value of b: "+Math.round(b));
		System.out.println("(Rounded)Nearest upper integear of b: "+Math.ceil(b));
		System.out.println("(Rounded)Nearest lower integear of b: "+Math.floor(b));
		
		
		
		
		
		
		
		

	}

}
